package com.company;

public class LC392 {
    public static void main(String[] args) {
        System.out.println(isSubsequence("leeeeetcode",
                ""));
    }
    public static boolean isSubsequence(String s, String t) {
        int ptrS = 0;
        int ptrT = 0;
        //不要增加多余的逻辑
        while(ptrS<s.length()&&ptrT<t.length()){

                if(t.charAt(ptrT)==s.charAt(ptrS)){
                    ptrS++;
                    ptrT++;
                }else {
                    ptrT++;
                }

        }
        if(ptrS==s.length())
            return true;
        return false;
    }


}
